mapper.select:
SELECT wb.business_id,wb.wf_id,wb.wf_title,wb.wf_type,wb.telephone,wb.gmt_create,wb.res_abnormal_id,wb.abnormal_type_id, wb.res_abnormallevel_id, wb.res_abnormaldesc,wb.url,wb.wo_eval_score,wb.current_step,wb.current_user,wb.wf_status, ws.hopetime, ws.deadtime, su.name AS userName, trb.res_id, trb.res_name, trb.res_alias, trb.res_ipv4, trb.res_address, rmi.res_abnormal_name, rmi.res_abnomaltime, tat.abnormal_type_name, tra.res_abnormallevel_name, trm.res_mtype_name, trs.res_stype_name, susu.name As currentUserName, twri.*, twdi.*, twoi.*, twhi.*, twci.*, twbai.*, twkai.*
FROM tbl_wf_business wb LEFT JOIN tbl_wo_sla ws ON wb.wo_sla_id = ws.wo_sla_id LEFT JOIN sys_user su ON wb.user_id =su.user_id LEFT JOIN tbl_res_mo_abnormal_info rmi ON wb.res_abnormal_id = rmi.res_abnormal_id LEFT JOIN tbl_abnormal_type tat ON wb.abnormal_type_id = tat.abnormal_type_id LEFT JOIN tbl_res_abnormallevel tra ON wb.res_abnormallevel_id = tra.res_abnormallevel_id LEFT JOIN tbl_res_base trb ON wb.res_id = trb.res_id LEFT JOIN tbl_res_maintype trm ON wb.res_mtype_id = trm.res_mtype_id LEFT JOIN tbl_res_subtype trs ON wb.res_stype_id = trs.res_stype_id LEFT JOIN sys_user susu ON wb.current_user = susu.user_id LEFT JOIN ( SELECT twri.business_id,twri.review_id,twri.dis_phone,twri.review_desc,twri.review_time,twri.review_user_id, (select name from sys_user where user_id=twri.review_user_id) AS currentReviewName, (select name from sys_user where user_id=twri.dis_user_id) AS disName FROM tbl_wf_review_info twri ) twri ON wb.business_id = twri.business_id LEFT JOIN ( SELECT twdi.dis_id,twdi.business_id,twdi.over_phone,twdi.dis_info,twdi.dis_time,twdi.dis_user_id, (select name from sys_user where user_id=twdi.dis_user_id) AS currentDisName, (select name from sys_user where user_id=twdi.over_user_id) AS overName FROM tbl_wf_dis_info twdi ) twdi ON wb.business_id = twdi.business_id LEFT JOIN ( SELECT twoi.business_id,twoi.over_id,twoi.over_info,twoi.over_file,twoi.over_type,twoi.over_time,twoi.handle_phone,twoi.over_user_id, (select name from sys_user where user_id=twoi.over_user_id) AS currentOverName, (select name from sys_user where user_id=twoi.handle_user_id) AS handleName FROM tbl_wf_over_info twoi ) twoi ON wb.business_id = twoi.business_id LEFT JOIN ( SELECT twhi.business_id,twhi.handle_id,twhi.confirm_phone,twhi.handle_file,twhi.handle_type,twhi.handle_info, twhi.handle_method, twhi.handle_time,twhi.handle_user_id, (select name from sys_user where user_id=twhi.handle_user_id) AS currentHandleName, (select name from sys_user where user_id=twhi.confirm_user_id) AS confirmName FROM tbl_wf_handle_info twhi ) twhi ON wb.business_id = twhi.business_id LEFT JOIN ( SELECT twci.business_id,twci.confirm_id,twci.base_appra_phone,twci.confirm_info,twci.confirm_time,twci.confirm_user_id, (select name from sys_user where user_id=twci.confirm_user_id) AS currentConfirmName, (select name from sys_user where user_id=twci.base_appra_user_id) AS baseAppraName FROM tbl_wf_confirm_info twci ) twci ON wb.business_id = twci.business_id LEFT JOIN ( SELECT twbai.business_id,twbai.base_appra_id,twbai.base_appra_score,twbai.kexin_appra_phone,twbai.base_appra_info,twbai.base_appra_time, (select name from sys_user where user_id=twbai.base_appra_user_id) AS currentBaseAppraName, (select name from sys_user where user_id=twbai.kexin_appra_user_id) AS kexinAppraName FROM tbl_wf_base_appra_info twbai ) twbai ON wb.business_id = twbai.business_id LEFT JOIN ( SELECT twkai.business_id,twkai.kexin_appra_id,twkai.kexin_appra_score,twkai.kexin_appra_time,twkai.kexin_appra_info, (select name from sys_user where user_id=twkai.kexin_appra_user_id) AS kexinAppraName FROM tbl_wf_kexin_appra_info twkai ) twkai ON wb.business_id = twkai.business_id WHERE wb.useflag=1
AND wb.wf_type = #{wfType}
AND wb.wf_title LIKE concat('%',#{wfTitle},'%')
AND tra.res_abnormallevel_name LIKE concat('%',#{resAbnormallevelName},'%')
AND wb.wf_status =#{wfStatus}
AND trb.res_name LIKE concat('%',#{resName},'%')
AND su.userName LIKE concat('%',#{userName},'%')
AND (twri.review_user_id=#{handleId} OR twdi.dis_user_id=#{handleId} OR twoi.over_user_id=#{handleId} OR twhi.handle_user_id=#{handleId} OR twci.confirm_user_id=#{handleId})
AND wb.user_id = #{userId}
AND wb.current_user = #{currentUser}
AND susu.username LIKE concat('%',#{currentUserName},'%')
AND wb.gmt_create >= #{beginTime}
AND wb.gmt_create <= #{endTime}
ORDER BY wb.gmt_create DESC

SELECT
wb.business_id,wb.wf_id,wb.wf_title,wb.wf_type,wb.telephone,wb.gmt_create,
wb.res_abnormaldesc,
ws.hopetime,
ws.deadtime,
trb.res_name,
tra.res_abnormallevel_name,
tat.abnormal_type_name,
trm.res_mtype_name,
trs.res_stype_name,
twri.*,
twbai.*,
twkai.*

FROM
tbl_wf_business wb
INNER JOIN tbl_wo_sla ws ON wb.wo_sla_id = ws.wo_sla_id
INNER JOIN tbl_res_abnormallevel tra ON wb.res_abnormallevel_id = tra.res_abnormallevel_id
INNER JOIN tbl_res_base trb ON wb.res_id = trb.res_id
INNER JOIN tbl_abnormal_type tat ON   wb.abnormal_type_id=tat.abnormal_type_id
INNER JOIN tbl_res_maintype trm ON wb.res_mtype_id = trm.res_mtype_id
INNER JOIN tbl_res_subtype trs ON wb.res_stype_id = trs.res_stype_id
INNER JOIN
(
SELECT business_id,(select name from sys_dept where dept_id = twri.dept_id) AS deptName FROM
tbl_wf_review_info twri
) twri
ON wb.business_id = twri.business_id
INNER JOIN
(
SELECT
twbai.business_id,twbai.base_appra_id,twbai.base_appra_score,twbai.base_appra_info,twbai.base_appra_time,
(select name from sys_user where user_id=twbai.base_appra_user_id) AS baseAppraName  FROM
tbl_wf_base_appra_info twbai
) twbai
ON wb.business_id = twbai.business_id
INNER JOIN
(
SELECT twkai.business_id,twkai.kexin_appra_id,twkai.kexin_appra_score,twkai.kexin_appra_time,twkai.kexin_appra_info,
(select name from sys_user where user_id=twkai.kexin_appra_user_id) AS kexinAppraName FROM
tbl_wf_kexin_appra_info twkai
) twkai
ON wb.business_id = twkai.business_id
WHERE wb.useflag=1

    and wb.wf_id=#{wfId}


    and wb.wf_title=#{wfTitle}


    and  baseAppraName=#{baseUserName}


    and kexinAppraName=#{kexinUserName}


    and  twbai.base_appra_time  >=  #{baseAppraBeginTime}


    and  twbai.base_appra_time   <=  #{baseAppraEndTime}


    and twkai.kexin_appra_time  >=  #{kexinAppraBeginTime}


    and twkai.kexin_appra_time   >=  #{kexinAppraEndTime}


and twbai.base_appra_score  >  #{baseAppraScore}


    and twbai.base_appra_score  <  #{baseAppraScore}


    and twbai.base_appra_score  =  #{baseAppraScore}


    and twbai.base_appra_score  >=  #{baseAppraScore}


    and twbai.base_appra_score  <=  #{baseAppraScore}


    and twkai.kexin_appra_score  >  #{kexinAppraScore}


    and twkai.kexin_appra_score  <  #{kexinAppraScore}


    and twkai.kexin_appra_score  =  #{kexinAppraScore}


    and twkai.kexin_appra_score  >=  #{kexinAppraScore}


    and twkai.kexin_appra_score  <=  #{kexinAppraScore}

 ORDER BY wb.gmt_create DESC
